package com.syntax.class01;

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// storing and representing whole numbers values
				byte box1 = 127;
				
				short box2=32767;
				
				int box3=100000; //most popular
						
				long box4=100000000000L;
		
		
		
		// storing and representing decimal numbers
				float variable1=1.99F;
		
				double variable2=99.0; // most popular
		
		
		//storing single characters
				char container='a';
		
				char dollar='$';
		
		
		//storage yes or no values (true or false)
		
				boolean hot=true;
				
				boolean tired=false;
				
				
				System.out.println(variable2); //printing value of the variable
				System.out.println("variable2"); // printing text variable
				
				System.out.println(tired);
				//System.out.println(variable3); error- variable does not exists
		
		
				
				
	
				
				
				
				
				
				
		
	}

}
