package com.syntax.class02;

public class NonprimitiveDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// String is just 1 example of non primitive data types
		// Anything inside between double quotes "" is String
		
		String name="Asma";
		String address="123 address street";
		String phone="123 456 7890";
		String age="17"; //Anything you put inside "" becomes a String
		String character = "A";
		
		
		//shortcut  for system.out.println= type syso than ctrl+space
		
		System.out.println(name);
		
		//"My name is" Asma
		System.out.println("My name is "+ name);
		
		//Asma is 17
		
		System.out.println(name + " is " + age);
		
		String fruit="apple";
		double price=1.99;
		
		// The price of the apple is 1.99
		
		System.out.println("The price of the "+fruit+" is "+price);
		
		
		fruit="mango";
		price=5.99;
		
		System.out.println("The price of the "+fruit+" is "+price);
		
		/* to attach any value (int, double,char, String, etc..)
		 * to a String we use + next toString acts as Concatenation
		 * 
		 * Concatenate=combining=adding
		 */
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}
	
}
