package com.syntax.class02;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name="Dusan"; 
		String lastname="Mijatovic"; 
		String grade="A";
		String city="Edgartown";
		String state="MA";
		String number="123-123-123"; 
		
		
		//A)
		
		System.out.println("My name is "+name+" and my last name is " +lastname+".");
		
		System.out.println("I'm "+grade+" grade student"+".");
		
		System.out.println("I live in "+city+" in a state of "+state+".");
		
		System.out.println("And my phone number is "+number+".");
		
		city="Indianapolis";
		state="Indiana";
		grade="B";
		number="231-231-231";
		
		
		//B))
		
		System.out.println("My name is "+name+" and I moved to "+city+" in "+state+".");
		System.out.println("My new phone number is "+number+".");
		
		
		
		
		
		
		//identifiers=names for variables, methods, classes
		
		//No.1 rule- cannot use key words as identifiers 
		//No.2 rule- we cannot have spaces in identifiers
		//No.3 rule-identifiers cannot start with numbers but it can contain number after first character 
		//No.4 rule-identifiers cannot contain any special characters except  $ or _  
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
