package com.syntax.class03;

public class RecapOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	/*
	 * addition=> +
	 * subtraction=> -
	 * multiplication=> *
	 * division=> /
	 * modulus=> %
	 */
		
		
		
		int number1=9;
		int number2=2;
		
		System.out.println("Answer = "+number1/number2);
		System.out.println("Remainder = "+number1%number2);

		

		double number3=9;
		double number4=2;
		
		System.out.println(number3/number4);// you have to change name of variables also, from num1,2 to num3,4
		
		

		float number5=1.0f;
		float number6=3.0f;
		
		System.out.println(number5/number6);

		

		double number7=1;
		double number8=3;
		
		System.out.println(number7/number8);
		
		
		
		
		
		
		
	}

}
