package com.syntax.class07;

public class DoWhileLoopDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		int num=1;
		
		while(num<=3) {
			
			System.out.println("I am while loop");
			num++;
		}		
		
		
		
		System.out.println("------------------------------");
		
		int num1=1;
		
		do {
			System.out.println("I am DO while loop");
			num1++;
		}while(num1>=3);
		
		
		
		

	}

}
