package com.syntax.class07;

public class WhileLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	int time=10;
	
	
	if(time<12) {
		
		
		System.out.println("good morning");
	
		
	}
		
		
		while(time<12) {
			
			System.out.println("Good morning");
			time++;
			
		}
		

		
		
		
		
		
		
	}

}
