package com.syntax.class09;

public class ArrayDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String[] drinks= {"fanta", "milk","juice","lemonade","water", "coke"};//0,1,2,3,4
		
		
		System.out.println(drinks[2]);//juice
		
		System.out.println(drinks[1]);
		
		//how many elements inside my array?
		
		int size=drinks.length;// will give you number of elements in the array
		
		System.out.println("Size of array="+size);
		
		
		
		
		
		
	}

}
