package com.syntax.class09;

public class ArrayFixedSize {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int [] nums=new int[3];
		
		nums[0]=12;
		nums[2]=13;
		
		System.out.println(nums[1]); // default value of String is NULL, of int,double etc is 0 or 0.0
		
		
		String[] colors=new String[3];
		
		colors[0]="white";
		colors[1]="pink";
		colors[2]="black";
		colors[3]="blue";//<---------extra element, syso wont run, mistake
		
		
		System.out.println(colors[3]);
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
